        </tr>
    </thead>
    <tbody>
        {
            filteredResults().map((u, i) => {
                let id = `actModal-${i}`
                console.log(id)
                return (
                <tr>
                <td>{u.username}</td>
                <td>{u.email}</td>
                <td >
                    {editUsers(u, i)}
    <div class="modal fade" id={`actModal-${i}`} tabindex="-1" aria-labelledby="actModalLabel" aria-hidden="true" onClick={() => {
      set_id(u._id)
